import React, { useEffect, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { getData, postData, putData, deleteData } from './services/ApiServices.js'; // Importing the ApiServices
import './App.css';

function App() {
  const { control, handleSubmit, reset } = useForm();
  const [tasks, setTasks] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [id, setId] = useState(null);

  const getAllTaskList = async () => {
    const url = "task/list/get/all";
    try {
      let result = await getData(url);
      if (!result || !result.length) {
        setError("Sorry no data found");
      }
      setTasks(result.map((task) => ({
        title: task.title,
        description: task.description || 'Sample description',
        deadline: task.deadline || '2024-10-30',
        completed: task.completed,
      })));
    } catch (e) {
      setError(e.message);
    }
  }

  // Fetch tasks from the API when the component mounts
  useEffect(() => {
    getAllTaskList();
  }, []);

  const onSubmit = async (data) => {
    if (id !== null) {
      const url = `task/list/${id}`;
      try {
        let result = await postData(url, data);
        setSuccess(result);
      } catch (e) {
        setSuccess("");
        setShowForm(false);
        setError(e.message);
      }
    } else {
      const url = "task/list/";
      try {
        let result = await postData(url, data);
        setSuccess(result);
      } catch (e) {
        setSuccess("");
        setShowForm(false);
        setError(e.message);
      }
    }
    reset();
  };

  const handleEdit = (id) => {
    setId(id);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    const url = `task/list/${id}`;
    try {
      let result = await deleteData(url);
      setSuccess(result);
    } catch (e) {
      setSuccess("");
      setError(e.message);
    }
    deleteData(`tasks/${index + 1}`)
      .then(() => {
        const updatedTasks = tasks.filter((_, i) => i !== index);
        setTasks(updatedTasks);
        setError(''); // Clear any previous error messages
      })
      .catch((error) => {
        setError('Error deleting task: ' + error.message);
      });
  };

  const toggleForm = () => {
    setShowForm(!showForm);
    setId(null);
    reset();
    setError('');
    setSuccess('');// Clear any previous error messages
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>
      {error && <div className="error-message">{error}</div>} {/* Displaying the error message */}
      {success && <div className="success-message">{success}</div>} {/* Displaying the success message */}

      <button className="add-button" onClick={toggleForm}>
        {showForm ? 'Close Form' : 'Add Task'}
      </button>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-button" onClick={toggleForm}>&times;</span>
            <form onSubmit={handleSubmit(onSubmit)} className="form">
              <label>
                Title:
                <Controller
                  name="title"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Title is required' }}
                  render={({ field }) => <input type="text" {...field} required />}
                />
              </label>
              <label>
                Description:
                <Controller
                  name="description"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Description is required' }}
                  render={({ field }) => <input type="text" {...field} required />}
                />
              </label>
              <label>
                Deadline:
                <Controller
                  name="deadline"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Deadline is required' }}
                  render={({ field }) => <input type="date" {...field} required />}
                />
              </label>
              <button type="submit">
                {id !== null ? 'Update' : 'Add'}
              </button>
            </form>
          </div>
        </div>
      )}

      <h2>Task List</h2>
      <div className="task-list">
        {tasks.map((task, index) => (
          <div className="task-card" key={index}>
            <div className="task-details">
              <h3>{task.title}</h3>
              <p className="description">{task.description}</p>
              <div className="deadline">Deadline: {task.deadline}</div>
            </div>
            <div className="task-actions">
              <button className="edit-button" onClick={() => handleEdit(task._id)}>Edit</button>
              <button className="delete-button" onClick={() => handleDelete(task._id)}>Delete</button>
            </div>
            <div className={`status-badge ${task.completed ? 'completed' : 'not-completed'}`}>
              {task.completed ? 'Completed' : 'Not Completed'}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
