import React, { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import './App.css';

function App() {
  const { control, handleSubmit, reset } = useForm();
  const [tasks, setTasks] = useState([
    { title: 'Task 1', description: 'Description 1', deadline: '2024-10-20', completed: false },
    { title: 'Task 2', description: 'Description 2', deadline: '2024-10-25', completed: true },
  ]);
  const [editIndex, setEditIndex] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const onSubmit = (data) => {
    if (editIndex !== null) {
      const updatedTasks = [...tasks];
      updatedTasks[editIndex] = data;
      setTasks(updatedTasks);
      setEditIndex(null);
    } else {
      setTasks([...tasks, { ...data, completed: false }]);
    }
    reset();
    setShowForm(false);
  };

  const handleEdit = (index) => {
    const taskToEdit = tasks[index];
    setEditIndex(index);
    reset(taskToEdit);
    setShowForm(true);
  };

  const handleDelete = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
  };

  const toggleForm = () => {
    setShowForm(!showForm);
    setEditIndex(null);
    reset();
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <button className="add-button" onClick={toggleForm}>
        {showForm ? 'Close Form' : 'Add Task'}
      </button>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-button" onClick={toggleForm}>&times;</span>
            <form onSubmit={handleSubmit(onSubmit)} className="form">
              <label>
                Title:
                <Controller
                  name="title"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Title is required' }}
                  render={({ field }) => <input type="text" {...field} required />}
                />
              </label>
              <label>
                Description:
                <Controller
                  name="description"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Description is required' }}
                  render={({ field }) => <input type="text" {...field} required />}
                />
              </label>
              <label>
                Deadline:
                <Controller
                  name="deadline"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Deadline is required' }}
                  render={({ field }) => <input type="date" {...field} required />}
                />
              </label>
              <button type="submit">
                {editIndex !== null ? 'Update' : 'Add'}
              </button>
            </form>
          </div>
        </div>
      )}

      <h2>Task List</h2>
      <div className="task-list">
        {tasks.map((task, index) => (
          <div className="task-card" key={index}>
            <div className="task-details">
              <h3>{task.title}</h3>
              <p className="description">{task.description}</p>
              <div className="deadline">Deadline: {task.deadline}</div>
            </div>
            <div className="task-actions">
              <button className="edit-button" onClick={() => handleEdit(index)}>Edit</button>
              <button className="delete-button" onClick={() => handleDelete(index)}>Delete</button>
            </div>
            <div className={`status-badge ${task.completed ? 'completed' : 'not-completed'}`}>
              {task.completed ? 'Completed' : 'Not Completed'}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
